mapper.select:
select *
from board
where category = #{category} 
and article_no = #{articleNo}

mapper.update:
update board
set title = #{title}, content = #{content}
where category = #{category}
and article_no = #{articleNo}

update board
set hit = hit + 1
where category = #{category}
and article_no = #{articleNo}

update board
set like = like + 1
where category = #{category}
and article_no = #{articleNo}

mapper.delete:
delete from board
where category = #{category}
and article_no = #{articleNo};
